import { useState } from 'react';

function AnimeCard({ animeData, onAddToWatchlist }) {
	const [review, setReview] = useState('');

	const handleReviewChange = (event) => {
		setReview(event.target.value);
	};

	const handleAddToWatchlist = () => {
		const newAnimeData = { ...animeData, review };
		onAddToWatchlist(newAnimeData);
		setReview('');
	};

	return (
		<div className='card mb-3'>
			<div className='row no-gutters'>
				<div className='col-md-4'>
					<img
						src={animeData.image_url}
						className='card-img'
						alt={animeData.title}
					/>
				</div>
				<div className='col-md-8'>
					<div className='card-body'>
						<h5 className='card-title'>{animeData.title}</h5>
						<p className='card-text'>{animeData.synopsis}</p>
						<ul className='list-group list-group-flush'>
							<li className='list-group-item'>
								Episodes: {animeData.episodes}
							</li>
							<li className='list-group-item'>Airing: {animeData.airing}</li>
							<li className='list-group-item'>Score: {animeData.score}</li>
							<li className='list-group-item'>
								<label htmlFor='review'>Review:</label>
								<input
									type='text'
									id='review'
									value={review}
									onChange={handleReviewChange}
								/>
							</li>
						</ul>
						<button
							type='button'
							className='btn btn-primary mr-2'
							onClick={handleAddToWatchlist}
						>
							Add to Watchlist
						</button>
						<button
							type='button'
							className='btn btn-secondary'
						>
							Cancel
						</button>
					</div>
				</div>
			</div>
		</div>
	);
}

export default AnimeCard;
