import React, { useState, useEffect } from 'react';
import HomePage from './components/HomePage';
import WatchlistPage from './components/WatchlistPage';
import api from './api';

function App() {
	const [animeList, setAnimeList] = useState([]);
	const [watchlist, setWatchlist] = useState([]);

	useEffect(() => {
		api.getRandomAnimeList().then((data) => setAnimeList(data));
		api.getWatchlist().then((data) => setWatchlist(data));
	}, []);

	const handleAddToWatchlist = (anime) => {
		api
			.addAnimeToWatchlist(anime)
			.then((data) => setWatchlist((prevList) => [...prevList, data]));
	};

	const handleUpdateReview = (id, review) => {
		api
			.updateAnimeReview(id, review)
			.then((data) =>
				setWatchlist((prevList) =>
					prevList.map((anime) => (anime.id === id ? data : anime))
				)
			);
	};

	const handleDeleteAnime = (id) => {
		api
			.deleteAnimeFromWatchlist(id)
			.then((data) =>
				setWatchlist((prevList) => prevList.filter((anime) => anime.id !== id))
			);
	};

	const [currentPage, setCurrentPage] = useState('home');

	const showHomePage = () => setCurrentPage('home');
	const showWatchlistPage = () => setCurrentPage('watchlist');

	let pageContent;
	if (currentPage === 'home') {
		pageContent = (
			<HomePage
				animeList={animeList}
				handleAddToWatchlist={handleAddToWatchlist}
				showWatchlistPage={showWatchlistPage}
			/>
		);
	} else if (currentPage === 'watchlist') {
		pageContent = (
			<WatchlistPage
				watchlist={watchlist}
				handleUpdateReview={handleUpdateReview}
				handleDeleteAnime={handleDeleteAnime}
				showHomePage={showHomePage}
			/>
		);
	}

	return <div className='App'>{pageContent}</div>;
}

export default App;
