import React, { useState } from 'react';

function AnimeCard({ anime, handleAddToWatchlist }) {
	const [review, setReview] = useState('');

	const handleReviewChange = (event) => {
		setReview(event.target.value);
	};

	const handleAddClick = () => {
		handleAddToWatchlist({ ...anime, review });
	};

	return (
		<div className='anime-card'>
			<img
				src={anime.image_url}
				alt={anime.title}
			/>
			<div>
				<h2>{anime.title}</h2>
				<ul>
					<li>Score: {anime.score}</li>
					<li>Airing: {anime.airing ? 'Yes' : 'No'}</li>
					<li>Episodes: {anime.episodes}</li>
					<li>Synopsis: {anime.synopsis}</li>
				</ul>
				<div className='review-form'>
					<label htmlFor={`review-${anime.title}`}>Review:</label>
					<textarea
						id={`review-${anime.title}`}
						value={review}
						onChange={handleReviewChange}
					/>
					<button onClick={handleAddClick}>Add to Watchlist</button>
				</div>
			</div>
		</div>
	);
}

export default AnimeCard;
