import React, { useState } from 'react';

function AnimeCard(props) {
	const [review, setReview] = useState('');

	const handleAddReview = (event) => {
		event.preventDefault();
		const anime = props.anime;
		anime.review = review;
		setReview('');
		props.onAddReview(anime);
	};

	const handleAddToWatchList = () => {
		props.onAddToWatchList(props.anime);
	};

	return (
		<div className='col mb-4'>
			<div className='card'>
				<img
					src={props.anime.image_url}
					className='card-img-top'
					alt={props.anime.title}
				/>
				<div className='card-body'>
					<h5 className='card-title'>{props.anime.title}</h5>
					<p className='card-text'>{props.anime.synopsis}</p>
				</div>
				<ul className='list-group list-group-flush'>
					<li className='list-group-item'>Episodes: {props.anime.episodes}</li>
					<li className='list-group-item'>Score: {props.anime.score}</li>
					<li className='list-group-item'>
						<form onSubmit={handleAddReview}>
							<div className='form-group'>
								<label htmlFor='review'>Add Review</label>
								<input
									type='text'
									className='form-control'
									id='review'
									value={review}
									onChange={(event) => setReview(event.target.value)}
								/>
							</div>
							<button
								type='submit'
								className='btn btn-primary'
							>
								Submit
							</button>
						</form>
					</li>
				</ul>
				<div className='card-body'>
					<button
						className='btn btn-primary'
						onClick={handleAddToWatchList}
					>
						Add to Watchlist
					</button>
				</div>
			</div>
		</div>
	);
}

export default AnimeCard;
