const API_BASE_URL = 'https://anime-watchlist-app-server.onrender.com/';

async function getRandomAnimeList() {
	const response = await fetch(`${API_BASE_URL}/getRandomAnime`);
	const data = await response.json();
	return Array.isArray(data) ? data : [data];
}

async function getWatchlist() {
	const response = await fetch(`${API_BASE_URL}/watchlist`);
	return response.json();
}

async function addAnimeToWatchlist(anime) {
	const response = await fetch(`${API_BASE_URL}/watchlist`, {
		method: 'POST',
		headers: { 'Content-Type': 'application/json' },
		body: JSON.stringify(anime),
	});
	return response.json();
}

async function updateAnimeReview(id, review) {
	const response = await fetch(`${API_BASE_URL}/watchlist/${id}`, {
		method: 'PUT',
		headers: { 'Content-Type': 'application/json' },
		body: JSON.stringify({ review }),
	});
	return response.json();
}

async function deleteAnimeFromWatchlist(id) {
	const response = await fetch(`${API_BASE_URL}/watchlist/${id}`, {
		method: 'DELETE',
	});
	return response.json();
}

export default {
	getRandomAnimeList,
	getWatchlist,
	addAnimeToWatchlist,
	updateAnimeReview,
	deleteAnimeFromWatchlist,
};
